-- * Funções principais da Tarefa 4.

-- | Avança o 'Estado' do jogo um 'Tick' de tempo.
--
-- __NB:__ Apenas os 'Disparo's afetam o 'Estado' do jogo com o passar do tempo.
--
-- __NB:__ Deve chamar as funções 'tickChoques', 'tickCanhoes' e 'tickLasers' pela ordem definida.
tick :: Estado -- ^ O 'Estado' anterior.
     -> Estado -- ^ O 'Estado' após um 'Tick'.
tick e = tickChoques (tickCanhoes(tickLasers e))

-- | Avança o 'Estado' do jogo um 'Tick' de tempo, considerando apenas os efeitos dos tiros de 'Laser' disparados.
tickLasers :: Estado -> Estado
tickLasers e = finalLaser e

-- | Avança o 'Estado' do jogo um 'Tick' de tempo, considerando apenas os efeitos das balas de 'Canhao' disparadas.
tickCanhoes :: Estado -> Estado
tickCanhoes  e@(Estado i o p) = tDCfinal (disparosEstado e) e

-- | Avança o 'Estado' do jogo um 'Tick' de tempo, considerando apenas os efeitos dos campos de 'Choque' disparados.
tickChoques :: Estado -> Estado
tickChoques e@(Estado i o p) = Estado i o (verfTiroChoque p) 

--verifica para uma ['Disparo'] se um 'DisparoChoque' tem de ser retirado ou decrementado o numero de tick ainda disponiveis. 
verfTiroChoque::[Disparo]->[Disparo]
verfTiroChoque [] = [] 
verfTiroChoque (h@(DisparoCanhao g k q ):hs) = [h]++ (verfTiroChoque hs)
verfTiroChoque (h@(DisparoLaser g k q ):hs) = [h]++ (verfTiroChoque hs)
verfTiroChoque (h@(DisparoChoque g k):hs) 
 | k>0 = [DisparoChoque g (k-1)]++(verfTiroChoque hs)
 | otherwise =(verfTiroChoque hs)

-- | Verfica se um disparo canhao bateu contra um outro disparo!INCOMPLETA
verifTiroVSTiro::Disparo->Estado->[Disparo]
verifTiroVSTiro d (Estado i o []) = []
verifTiroVSTiro (DisparoLaser w (e1,e2) r ) e = disparosEstado e
verifTiroVSTiro (DisparoChoque w  r ) e = disparosEstado e
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) (Estado i o (h@(DisparoChoque g q ):hs) )=[h] ++ verifTiroVSTiro d (Estado i o hs) 
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) est@(Estado i o (h@(DisparoCanhao g (k1,k2) q ):hs) )= if (e1==k1 && e2==k2 && r/=q) || (e1==k1 && e2==k2-1 && r==E && q==D)  || ( e1==k1-1 && e2==k2  && r==C && q==B) then verifTiroVSTiro d (Estado i o hs) else [h]++ verifTiroVSTiro d (Estado i o hs)
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) est@(Estado i o (h@(DisparoLaser g (k1,k2) C ):hs) ) = let (xf,yf)= (ateLaserDispC (k1,k2) est) in (if ((e1<=xf && e1>=k1) && e2==k2) then verifTiroVSTiro d (Estado i o hs) else [h]++ (verifTiroVSTiro d (Estado i o hs)) )
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) est@(Estado i o (h@(DisparoLaser g (k1,k2) B ):hs) ) = let (xf,yf)= (ateLaserDispC (k1,k2) est) in (if ((e1<=k1 && e1>=xf) && e2==k2) then verifTiroVSTiro d (Estado i o hs) else [h]++ (verifTiroVSTiro d (Estado i o hs)) )
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) est@(Estado i o (h@(DisparoLaser g (k1,k2) E ):hs) ) = let (xf,yf)= (ateLaserDispC (k1,k2) est) in (if e1==k1 && (e2<=k2 && e2>=yf) then verifTiroVSTiro d (Estado i o hs) else [h]++ (verifTiroVSTiro d (Estado i o hs)) )
verifTiroVSTiro d@(DisparoCanhao w (e1,e2) r ) est@(Estado i o (h@(DisparoLaser g (k1,k2) D ):hs) ) = let (xf,yf)= (ateLaserDispC (k1,k2) est) in (if e1==k1 && (e2<=yf && e2>=k2) then verifTiroVSTiro d (Estado i o hs) else [h]++ (verifTiroVSTiro d (Estado i o hs)) )



-- | Verifica se o disparoCANHAO bateu contra algum Jogador e caso afirmativo devolve true 
vTankDispCo2::Disparo->[Jogador]->[Bool]
vTankDispCo2  (DisparoCanhao a (x,y) c) [] = []
vTankDispCo2  (DisparoCanhao a (x,y) C) (h@(Jogador (x1,y1) c l k j):hs) = let (x1,y1)=posicaoJogador h 
 in (if ( x1==(x-1) && y1==y-1 || x1==(x-1) && y==y1 || x1==(x-1) && y1==y+1|| x1==(x-1) && y1==y-1) && (vidasJogador h > 0) then (True:vTankDispCo2  (DisparoCanhao a (x,y) C) hs) 
                                                                              else  (False:vTankDispCo2  (DisparoCanhao a (x,y) C) hs) )
vTankDispCo2  (DisparoCanhao a (x,y) B) (h@(Jogador (x1,y1) c l k j):hs) = let (x1,y1)=posicaoJogador h 
 in (if ( x1==(x+1) && y1==y-1 || x1==(x+1) && y1==y || x1==(x+1) && y1==y+1 ) && (vidasJogador h > 0) then (True:vTankDispCo2  (DisparoCanhao a (x,y) B) hs) 
                                                                              else  (False:vTankDispCo2  (DisparoCanhao a (x,y) B) hs) )
vTankDispCo2  (DisparoCanhao a (x,y) E) (h@(Jogador (x1,y1) c l k j):hs) = let (x1,y1)=posicaoJogador h 
 in (if ( x1==(x-1) && y1==y-1 || x1==x && y1==y-1 || x1==(x+1) && y1==y-1|| x1==(x-1) && y1==y) && (vidasJogador h > 0) then (True:vTankDispCo2  (DisparoCanhao a (x,y) E) hs) 
                                                                              else  (False:vTankDispCo2  (DisparoCanhao a (x,y) E) hs) )
vTankDispCo2  (DisparoCanhao a (x,y) D) (h@(Jogador (x1,y1) c l k j):hs) = let (x1,y1)=posicaoJogador h 
 in (if ( x1==(x-1) && y1==y+1 || x1==x && y1==y+1 || x1==(x+1) && y1==y+1) && (vidasJogador h > 0) then (True:vTankDispCo2  (DisparoCanhao a (x,y) D) hs) 
                                                                              else  (False:vTankDispCo2  (DisparoCanhao a (x,y) D) hs) )
vTankDispCo2 _ _ = []


