vtcp::[Disparo]->Estado->Estado
vtcp [] e = e
vtcp (c@(DisparoLaser a (x,y) h):t) e=vtcp t e
vtcp (c@(DisparoChoque a h):t) e=vtcp t e
vtcp (c@(DisparoCanhao a (x,y) h):t) e 
 | h ==C = vtcp t (verificaTiroCParedeC c e)
 | h ==B = vtcp t (verificaTiroCParedeB c e)
 | h ==E = vtcp t (verificaTiroCParedeE c e)
 | h ==D = vtcp t (verificaTiroCParedeD c e)


-- | Função que remove um 'Disparo' da ['Disparo']
removeDisparoList::Disparo->[Disparo]->[Disparo]
removeDisparoList disp [] = []
removeDisparoList disp (h:hs) =if (disp==h)then hs else [h]++ removeDisparoList disp hs

--movimenta um disparo
movDisp::Disparo->Disparo
movDisp (DisparoChoque g k) = DisparoChoque g k
movDisp (DisparoLaser g (e1,e2) p)  =DisparoLaser g (e1,e2) p 
movDisp (DisparoCanhao g (e1,e2) C)  =DisparoCanhao g (e1-1,e2) C 
movDisp (DisparoCanhao g (e1,e2) B)  =DisparoCanhao g (e1+1,e2) B 
movDisp (DisparoCanhao g (e1,e2) E)  =DisparoCanhao g (e1,e2-1) E 
movDisp (DisparoCanhao g (e1,e2) D)  =DisparoCanhao g (e1,e2+1) D 

subDisp::Disparo->[Disparo]->[Disparo]
subDisp d [] = []
subDisp d (h:hs) |h==d =(movDisp d):hs
                 |otherwise = [h]++ (subDisp d hs)  
--a variavel  int comeca sempre a zero.... e identifica o Id do jogador
removeVidaseDisp::Disparo->Int->[Bool]->Estado->Estado
removeVidaseDisp disp a [] (Estado i o p) = (Estado i o p) 
removeVidaseDisp disp a (h:hs) e@(Estado i o p)  | h==True = removeVidaseDisp disp (a+1) hs (retira1vida a (Estado i o (removeDisparoList disp p)) )
                                                 | h==False =  removeVidaseDisp disp (a+1) hs (Estado i o p)
                                                 --and(h:hs)==False && p/=verifTiroVSTiro disp e = Estado i o (removeDisparoList disp (verifTiroVSTiro disp  e ))
                                                 --and(h:hs)==False && p==verifTiroVSTiro disp e = Estado i o (removeDisparoList disp (subDisp disp p)) --o Disparo continua em frente (MOVER)

-- movimenta os disparos 
verDisp::Disparo->[Bool]->Estado->Estado
verDisp disp [] e=e
verDisp disp (h:hs) e@(Estado i o p) = 
 if (and(h:hs)==False && p==verifTiroVSTiro disp e ) 
  then  Estado i o (removeDisparoList disp (subDisp disp p))
 else if (and(h:hs)==False && p/=verifTiroVSTiro disp e )
  then Estado i o (removeDisparoList disp (verifTiroVSTiro disp  e ))
 else e



