--Lista de  posicoes afetada pelo laser orientado para cima!!!!!!!!!!!
lpaplC::Disparo->Posicao->[Posicao]
lpaplC d@(DisparoLaser a (x,y) c ) (xf,yf) 
 | xf<=x && y==yf =[(xf,yf),(xf,yf+1)] ++ lpaplC d (xf+1,yf) 
 | otherwise = []

--Lista de  posicoes no MAPA afetada pelo laser orientado para Baixo!!!!!!!!!!!
lpaplB::Disparo->Posicao->[Posicao]
lpaplB d@(DisparoLaser a (x,y) c ) (xf,yf) 
 | xf>=x && y==yf =[(xf,yf),(xf,yf+1)] ++ lpaplB d (xf-1,yf) 
 | otherwise = []


--Lista de  posicoes afetada pelo laser orientado para Esquerda!!!!!!!!!!!
lpaplE::Disparo->Posicao->[Posicao]
lpaplE d@(DisparoLaser a (x,y) c ) (xf,yf) 
 | xf==x && yf<=y =[(xf,yf),(xf+1,yf)] ++ lpaplE d (xf,yf+1) 
 | otherwise = []

--Lista de  posicoes afetada pelo laser orientado para Direita!!!!!!!!!!!
lpaplD::Disparo->Posicao->[Posicao]
lpaplD d@(DisparoLaser a (x,y) c ) (xf,yf) 
 | xf==x && yf>=y =[(xf,yf),(xf+1,yf)] ++ lpaplD d (xf,yf-1) 
 | otherwise = []

-- | Muda a lista de Posiçoes para a 'Peca' 'Vazia'
encontraP::[Posicao]->Mapa->Mapa
encontraP [] i = i
encontraP (h:hs) i = if (encpm h i ==Bloco Destrutivel) then encontraP hs (atualizaPosicaoMatrizM h Vazia i) else (encontraP hs i)


--Para todos os Disparos LAser faz as execucoes necessarias no mapa.
paraTDispL::[Disparo] -> Estado -> Estado
paraTDispL [] e = e
paraTDispL (h@(DisparoChoque  c d):t) e@(Estado i o p)=paraTDispL t e
paraTDispL (h@(DisparoCanhao  c (x,y) d):t) e@(Estado i o p)=paraTDispL t e
paraTDispL (h@(DisparoLaser  c (x,y) d):t) e@(Estado i o p)
 | d==C = paraTDispL t (Estado (encontraP (lpaplC h (ateLaserDispC1 (x,y) e) ) i) o p)
 | d==B = paraTDispL t (Estado (encontraP (lpaplB h (ateLaserDispB1 (x,y) e) ) i) o p)
 | d==E = paraTDispL t (Estado (encontraP (lpaplE h (ateLaserDispE1 (x,y) e) ) i) o p) 
 | d==D = paraTDispL t (Estado (encontraP (lpaplD h (ateLaserDispD1 (x,y) e) ) i) o p)
 | otherwise = paraTDispL t e


-- |deteta se um 'DisparoLaser' bate num jogador devolve-o para uma lista
vTankDispL1 :: Disparo->Estado->[Jogador]
vTankDispL1  (DisparoLaser a (x,y) C) (Estado i [] p) = []
vTankDispL1  (DisparoLaser a (x,y) B) (Estado i [] p) = []
vTankDispL1  (DisparoLaser a (x,y) E) (Estado i [] p) = []
vTankDispL1  (DisparoLaser a (x,y) D) (Estado i [] p) = []
vTankDispL1  (DisparoLaser a (x,y) C) (Estado i (h@(Jogador (x1,y1) t l k j):hs) p) = let (x2,y2)=ateLaserDispC1 (x,y) (Estado i (h:hs)  p)  in (if (x1<=x && x1 >=x2) && (y1==y || y1==y+1 || y1==y-1) then [h]++ (vTankDispL1 (DisparoLaser a (x,y) C) (Estado i hs p) ) else  (vTankDispL1 (DisparoLaser a (x,y) C) (Estado i hs p) ) )
vTankDispL1  (DisparoLaser a (x,y) B) (Estado i (h@(Jogador (x1,y1) t l k j):hs) p) = let (x2,y2)=ateLaserDispB1 (x,y) (Estado i (h:hs)  p)  in (if (x1>=x && x1 <=x2) && (y1==y || y1==y+1 || y1==y-1) then [h]++ (vTankDispL1 (DisparoLaser a (x,y) B) (Estado i hs p) ) else  (vTankDispL1 (DisparoLaser a (x,y) B) (Estado i hs p) ) )
vTankDispL1  (DisparoLaser a (x,y) E) (Estado i (h@(Jogador (x1,y1) t l k j):hs) p) = let (x2,y2)=ateLaserDispE1 (x,y) (Estado i (h:hs)  p)  in (if (y1<=y && y1 >=y2) && (x1==x || x1==x+1 || x1==x-1) then [h]++ (vTankDispL1 (DisparoLaser a (x,y) E) (Estado i hs p) ) else  (vTankDispL1 (DisparoLaser a (x,y) E) (Estado i hs p) ) )
vTankDispL1  (DisparoLaser a (x,y) D) (Estado i (h@(Jogador (x1,y1) t l k j):hs) p) = let (x2,y2)=ateLaserDispD1 (x,y) (Estado i (h:hs)  p)  in (if (y1>=y && y1 <=y2) && (x1==x || x1==x+1 || x1==x-1) then [h]++ (vTankDispL1 (DisparoLaser a (x,y) D) (Estado i hs p) ) else  (vTankDispL1 (DisparoLaser a (x,y) D) (Estado i hs p) ) )
vTankDispL1 _ e = []

--Da-nos todos os jogadores afetados por DIsparoslAser
