-- | Devolve 'Posicao' final em um 'laser' com direção para cima.
ateLaserDispC1 :: Posicao->Estado->Posicao
ateLaserDispC1 (x1,y1) (Estado i o s) | (encpm (x1,y1) i /=(Bloco Indestrutivel) && encpm (x1,y1+1) i /=(Bloco Indestrutivel) ) =  ateLaserDispC1 (x1-1,y1) (Estado i o s)
ateLaserDispC1 (x1,y1) (Estado i o s) | (encpm (x1,y1) i == (Bloco Indestrutivel) || encpm (x1,y1+1) i ==(Bloco Indestrutivel) ) =(x1,y1)  

-- | Devolve 'Posicao' final em um 'laser' com direção para baixo.
ateLaserDispB1 :: Posicao->Estado->Posicao
ateLaserDispB1 (x1,y1) (Estado i o s) | (encpm (x1+1,y1) i /=(Bloco Indestrutivel) && encpm (x1+1,y1+1) i /=(Bloco Indestrutivel) ) =  ateLaserDispB1 (x1+1,y1) (Estado i o s)
ateLaserDispB1 (x1,y1) (Estado i o s) | (encpm (x1+1,y1) i == (Bloco Indestrutivel) || encpm (x1+1,y1+1) i ==(Bloco Indestrutivel) ) =(x1,y1)  

-- | Devolve 'Posicao' final em um 'laser' com direção para a esquerda.
ateLaserDispE1 :: Posicao->Estado->Posicao
ateLaserDispE1 (x1,y1) (Estado i o s) | (encpm (x1+1,y1) i /=(Bloco Indestrutivel) && encpm (x1,y1) i /=(Bloco Indestrutivel) ) =  ateLaserDispE1 (x1,y1-1) (Estado i o s)
ateLaserDispE1 (x1,y1) (Estado i o s) | (encpm (x1+1,y1) i == (Bloco Indestrutivel) || encpm (x1,y1) i ==(Bloco Indestrutivel) ) =(x1,y1)  

-- | Devolve 'Posicao' final em um 'laser' com direção para a direita.
ateLaserDispD1 :: Posicao->Estado->Posicao
ateLaserDispD1 (x1,y1) (Estado i o s) | (encpm (x1,y1+1) i /=(Bloco Indestrutivel) && encpm (x1+1,y1+1) i /=(Bloco Indestrutivel) ) =  ateLaserDispD1 (x1,y1+1) (Estado i o s)
ateLaserDispD1 (x1,y1) (Estado i o s) | (encpm (x1,y1+1) i == (Bloco Indestrutivel) || encpm (x1+1,y1+1) i ==(Bloco Indestrutivel) ) =(x1,y1)  

--Elimina canhoes se o laser lhe passar por cima!
burnCanhoes::Disparo->[Disparo]->Estado->[Disparo]
burnCanhoes (DisparoLaser b (x,y) c ) [] e = []
burnCanhoes (DisparoLaser b (x,y) c ) (h@(DisparoChoque c1 d):hs) e = [h]++burnCanhoes (DisparoLaser b (x,y) c ) hs e
burnCanhoes (DisparoLaser b (x,y) c ) (h@(DisparoLaser  c1 (x1,y1) d):hs) e =[h]++ burnCanhoes (DisparoLaser b (x,y) c ) hs e

burnCanhoes (DisparoLaser b (x,y) C ) (h@(DisparoCanhao  c (x1,y1) d):hs) e =let (x2,y2)=ateLaserDispC1 (x,y) e 
 in (if (x1<=x && x1 >=x2) && (y1==y) then burnCanhoes (DisparoLaser b (x,y) C ) hs e else [h]++ burnCanhoes (DisparoLaser b (x,y) C ) hs e)

burnCanhoes (DisparoLaser b (x,y)  B ) (h@(DisparoCanhao  c (x1,y1) d):hs) e =let (x2,y2)=ateLaserDispB1 (x,y) e 
 in (if (x1>=x && x1 <=x2) && (y1==y) then burnCanhoes (DisparoLaser b (x,y) B ) hs e else [h]++ burnCanhoes (DisparoLaser b (x,y) B ) hs e)

burnCanhoes (DisparoLaser b (x,y)  E ) (h@(DisparoCanhao  c (x1,y1) d):hs) e =let (x2,y2)=ateLaserDispE1 (x,y) e 
 in (if (y1<=y && y1 >=y2) && (x1==x) then burnCanhoes (DisparoLaser b (x,y) E ) hs e else [h]++ burnCanhoes (DisparoLaser b (x,y) E ) hs e)

burnCanhoes (DisparoLaser b (x,y)  D ) (h@(DisparoCanhao  c (x1,y1) d):hs) e =let (x2,y2)=ateLaserDispD1 (x,y) e 
 in (if (y1>=y && y1 <=y2) && (x1==x) then burnCanhoes (DisparoLaser b (x,y) D ) hs e else [h]++ burnCanhoes (DisparoLaser b (x,y) D ) hs e)
burnCanhoes _ [] _ = []
burnCanhoes _ a e =a 


burnSemiFinal::[Disparo]->Estado->Estado
burnSemiFinal [] e = e
burnSemiFinal (x:xs) e@(Estado i o (h:hs)) = burnSemiFinal xs (Estado i o (burnCanhoes x (h:hs) e) )



