import LI11819
import Tarefa2_2018li1g003
import Tarefa3_2018li1g003
import Tarefa4_2018li1g003
import Graphics.Gloss
import Graphics.Gloss.Interface.Pure.Game

-- | Função principal da Tarefa 5.
--
-- __NB:__ Esta Tarefa é completamente livre. Deve utilizar a biblioteca <http://hackage.haskell.org/package/gloss gloss> para animar o jogo, e reutilizar __de forma completa__ as funções das tarefas anteriores.
main :: IO ()
main = do estadio  <- loadBMP "images/estadio.bmp"
          jog1     <- loadBMP "images/jog1.bmp"
          jog2     <- loadBMP "images/jog2.bmp"
          jog3     <- loadBMP "images/jog3.bmp"
          jog4     <- loadBMP "images/jog4.bmp"
          bola     <- loadBMP "images/bola.bmp"
          carrinho <- loadBMP "images/carrinho.bmp"
          trocada  <- loadBMP "images/trocada.bmp"
          win1     <- loadBMP "images/winner1.bmp"
          win2     <- loadBMP "images/winner2.bmp"
          win3     <- loadBMP "images/winner3.bmp"
          win4     <- loadBMP "images/winner4.bmp"
          campo    <- loadBMP "images/campo.bmp"
          play window                        -- janela onde irá correr o jogo
               background                    -- côr do fundo da janela
               fr                            -- frame rate
               (x,y,estadoInicial,[estadio,Translate (-850) 400 win1,Translate 850 400 win2,Translate (-850) (-400) win3,Translate 850 (-400) win4, Pictures (aux1 x y mapaFut [[bola],[carrinho],[campo],[jog1,jog2,jog3,jog4],[win1,win2,win3,win4],[campo]])],[[bola],[carrinho],[trocada],[jog1,jog2,jog3,jog4],[win1,win2,win3,win4],[campo]]) -- estado inicial
               desenhaEstadoGloss            -- desenha o estado do jogo --b
               reageEventoGloss              -- reage a um evento
               reageTempoGloss               -- reage ao passar do tempo

-- | Janela de exibição.
window :: Display
window = FullScreen
{-window = InWindow "TANKS"     -- título da janela
                  (1000, 1000)-- dimensão da janela
                  (0,0)       -- posição no ecrã 
-}
-- | Cor do fundo em exibição.
background :: Color
background = greyN 0.8

-- | Frame rate escolhido.
fr :: Int
fr = 4

