-- ** Desenha Estado

-- | Converte um estado em imagem. (Usa três funções auxiliares, 'aux1' para o mapa, 'aux2' para os jogadores e 'aux3' para os disparos.)
desenhaEstado:: Float -> Float -> Estado -> [[Picture]] -> Picture
desenhaEstado x y (Estado m jogs disp) [a,b,c,d,e,f] 
  | vidasJogador (head jogs)>0 && vidasJogador ((!!) jogs 1)<=0 && vidasJogador ((!!) jogs 2)<=0 && vidasJogador ((!!) jogs 3)<=0 = Scale 4.0 4.0 (head e) -- vencedores
  | vidasJogador ((!!) jogs 1)>0 && vidasJogador (head jogs)<=0 && vidasJogador ((!!) jogs 2)<=0 && vidasJogador ((!!) jogs 3)<=0 = Scale 4.0 4.0 (e !! 1)
  | vidasJogador ((!!) jogs 2)>0 && vidasJogador (head jogs)<=0 && vidasJogador ((!!) jogs 1)<=0 && vidasJogador ((!!) jogs 3)<=0 = Scale 4.0 4.0 (e !! 2)
  | vidasJogador ((!!) jogs 3)>0 && vidasJogador (head jogs)<=0 && vidasJogador ((!!) jogs 1)<=0 && vidasJogador ((!!) jogs 2)<=0 = Scale 4.0 4.0 (e !! 3)
  | otherwise= Pictures (aux1 x y m [a,b,c,d,e,f]++aux3 x y jogs m disp [a,b,c,d,e,f]++aux2 x y jogs [a,b,c,d,e,f]) -- mapa,disparos e jogadores ficam sobrepostos por esta ordem.
 
-- | Abcissa inicial.
x :: Float
x = -625
-- | Ordenada inicial.
y :: Float
y = 275

-- | converte mapa em imagens.
aux1 :: Float -> Float -> Mapa -> [[Picture]] -> [Picture] -- Mapa = [[Pecas]]
aux1 x y [] [a,b,c,d,e,f] = []
aux1 x y (h:t) [a,b,c,d,e,f] = linM x y h [a,b,c,d,e,f] ++ aux1 x (y-25) t [a,b,c,d,e,f]

-- | converte linhas de mapa em imagens. Tendo em conta que cada peça do mapa tem dimensão 25 pixeis por 25.
linM :: Float -> Float -> [Peca] ->[[Picture]] ->[Picture]
linM x y [] [a,b,c,d,e,f] = []
linM x y (h:t) [a,b,c,d,e,f] | h == Bloco Indestrutivel = Translate x y blocI : linM (x+25) y t [a,b,c,d,e,f]
                             | h == Bloco Destrutivel   = Translate x y blocD : linM (x+25) y t [a,b,c,d,e,f]
                             | h == Vazia               = Translate (x+12.5) (y+12.5) (head f) : linM (x+25) y t [a,b,c,d,e,f]
-- | Representa blocos indestrutível.
blocI :: Picture
blocI = Pictures [qI]
-- | Representa bloco destrutível.
blocD :: Picture
blocD = Pictures [qD]
-- | Quadrado branco que servirá de base para blocos destrutíveis.
qD :: Picture
qD = Color white (Polygon [(0,0),(0,25),(25,25),(25,0),(0,0)])
-- | Quadrado verde que servirá de base para blocos indestrutíveis.
qI :: Picture
qI = Color (light green) (Polygon [(0,0),(0,25),(25,25),(25,0),(0,0)])
{-
-- | Representa bloco indestrutível.
blocInd :: Picture
blocInd = Pictures [qI]
-- | Quadrado azul (indestrutivel) que servirá de base para blocos indestrutiveis.
qI :: Picture
qI = Color (mixColors 0.5 0.5 blue black) (Polygon [(0,0),(0,25),(25,25),(25,0),(0,0)])
-- | Linha a volta do quadrado.
contornoQ :: Picture
contornoQ = Line [(0,0),(0,25),(25,25),(25,0),(0,0)]
-}
