aux2 :: Float -> Float -> [Jogador] -> [[Picture]] -> [Picture]
aux2 x y [] [a,b,c,_,e,f] = []
aux2 x y (h:t) [a,b,c,d:ds,e,f] | vidasJogador h >0 = Translate (x+fromIntegral((1+snd(posicaoJogador h))*25)) (y-fromIntegral(fst(posicaoJogador h)*25)) 
                ( rodaTank (direcaoJogador h) d) : aux2 x y t [a,b,c,ds,e,f]
                              | otherwise = aux2 x y t [a,b,c,ds,e,f]
