-- | Assegura a rotação de um tank.
rodaTank :: Direcao -> Picture -> Picture
rodaTank C p = p            -- os tanks estao inicialmente orientados para a cima.
rodaTank D p = rotate 90 p
rodaTank B p = rotate 180 p   
rodaTank E p = rotate 270 p  

-- | Converte disparos em imagens.
aux3 :: Float -> Float -> [Jogador] -> Mapa -> [Disparo] -> [[Picture]] -> [Picture]
aux3 x y jogs m [] (p:ps) = []
aux3 x y jogs m (DisparoCanhao jD pD dD:t) [a,b,c,d,e,f] = Translate (x+fromIntegral((1+snd pD)*25)) (y-fromIntegral(fst pD*25)) (rodaDisparo dD (head a)) : aux3 x y jogs m t [a,b,c,d,e,f]
aux3 x y jogs m (DisparoLaser jD pD dD:t) [a,b,c,d,e,f] | dD == B || dD == D = imagensLaser x y (init(laserP (DisparoLaser jD pD dD) m)) (rodaDisparo dD (head b)) ++ aux3 x y jogs m t [a,b,c,d,e,f] -- init em cima (posicao extra).
                                                        | dD == E || dD == C = imagensLaser x y (laserP (DisparoLaser jD pD dD) m) (rodaDisparo dD (head b)) ++ aux3 x y jogs m t [a,b,c,d,e,f]
aux3 x y jogs m (DisparoChoque jD _:t) [a,b,c,d,e,f]  | jD == 0 = Translate (x+fromIntegral((1+snd(posicaoJogador (head jogs)))*25))                 -- valor x do jogador
                                                                                              (y-fromIntegral(fst(posicaoJogador (head jogs))*25))   -- valor y do jogador
                                                                                              (head c) : aux3 x y jogs m t [a,b,c,d,e,f]
                                                      | jD == 1 = Translate (x+fromIntegral((1+snd(posicaoJogador ( (!!) jogs 1)))*25))
                                                                                              (y-fromIntegral(fst(posicaoJogador( (!!) jogs 1))*25))
                                                                                              (head c) : aux3 x y jogs m t [a,b,c,d,e,f]
                                                      | jD == 2 = Translate (x+fromIntegral((1+snd(posicaoJogador ( (!!) jogs 2)))*25)) 
                                                                                              (y-fromIntegral(fst(posicaoJogador( (!!) jogs 2))*25))
                                                                                              (head c) : aux3 x y jogs m t [a,b,c,d,e,f]
                                                      | jD == 3 = Translate (x+fromIntegral((1+snd(posicaoJogador ( (!!) jogs 3)))*25))
                                                                                              (y-fromIntegral(fst(posicaoJogador( (!!) jogs 3))*25))
                                                                                              (head c) : aux3 x y jogs m t [a,b,c,d,e,f]
-- | Assegura a rotação de um disparo.
rodaDisparo :: Direcao -> Picture -> Picture
rodaDisparo C p = p           -- os disparos estao inicialmente orientados para cima.
rodaDisparo D p = rotate 90 p
rodaDisparo B p = rotate 180 p
rodaDisparo E p = rotate 270 p

-- | Coloca a imagem do disapro laser em todas as posicoes que o mesmo percorre.
imagensLaser:: Float -> Float -> [Posicao] -> Picture -> [Picture]
imagensLaser x y t p = map (\ h -> Translate (x + fromIntegral ((1 + snd h) * 25)) (y - fromIntegral (fst h * 25)) p) t
