-- | Lista as posiçoes a frente do laser.
laserP :: Disparo -> Mapa -> [Posicao]
laserP (DisparoLaser i (a,b) d) m 
 |d == C = if encpm (a,b) m == Bloco Indestrutivel || encpm (a,b+1) m == Bloco Indestrutivel then []
                                       else (a,b): laserP (DisparoLaser i (a-1,b) d) m
 |d == B = if encpm (a,b) m == Bloco Indestrutivel || encpm (a,b+1) m == Bloco Indestrutivel then []
                                       else (a,b): laserP (DisparoLaser i (a+1,b) d) m              -- init em cima
 |d == E = if encpm (a,b) m == Bloco Indestrutivel || encpm (a+1,b) m == Bloco Indestrutivel then []
                                       else (a,b): laserP (DisparoLaser i (a,b-1) d) m
 |d == D = if encpm (a,b) m == Bloco Indestrutivel || encpm (a+1,b) m == Bloco Indestrutivel then []
                                       else (a,b): laserP (DisparoLaser i (a,b+1) d) m              -- init em cima
 |otherwise = []
