-- ** Reage Evento
-- | Reage a um 'Event'o no teclado.
reageEvento :: Event -> Estado -> Estado
reageEvento (EventKey (SpecialKey KeyEnter)  Down _ _) est = estadoInicial                 -- mapaInicial.
reageEvento (EventKey (SpecialKey KeySpace)  Down _ _) est = Estado sub0 [Jogador (1,1) D 5 3 3, Jogador (1,47) B 5 3 3, Jogador (23,1)  C 5 3 3, Jogador (23,47) E 5 3 3] [] -- outros mapas.            
reageEvento (EventKey (SpecialKey KeyShiftL) Down _ _) est = Estado sub1 [Jogador (1,1) E 5 3 3, Jogador (1,47) B 5 3 3, Jogador (23,1)  D 5 3 3, Jogador (23,47) C 5 3 3] []                  
reageEvento (EventKey (SpecialKey KeyCtrlL)  Down _ _) est = Estado sub2 [Jogador (1,1) B 5 3 3, Jogador (1,47) C 5 3 3, Jogador (23,1)  D 5 3 3, Jogador (23,47) E 5 3 3] []                  
reageEvento (EventKey (SpecialKey KeyAltL)   Down _ _) est = Estado sub3 [Jogador (1,1) D 5 3 3, Jogador (1,47) B 5 3 3, Jogador (23,1)  C 5 3 3, Jogador (23,47) E 5 3 3] []
reageEvento (EventKey (SpecialKey KeyTab)    Down _ _) est = Estado sub4 [Jogador (1,1) D 5 3 3, Jogador (1,47) B 5 3 3, Jogador (23,1)  C 5 3 3, Jogador (23,47) E 5 3 3] []
reageEvento (EventKey (SpecialKey KeyUp)    Down _ _) est = jogada 0 (Movimenta C)     est -- jogador 0.
reageEvento (EventKey (SpecialKey KeyDown)  Down _ _) est = jogada 0 (Movimenta B)     est
reageEvento (EventKey (SpecialKey KeyLeft)  Down _ _) est = jogada 0 (Movimenta E)     est
reageEvento (EventKey (SpecialKey KeyRight) Down _ _) est = jogada 0 (Movimenta D)     est
reageEvento (EventKey (Char ',')            Down _ _) est = jogada 0 (Dispara Canhao)  est
reageEvento (EventKey (Char '.')            Down _ _) est = jogada 0 (Dispara Laser )  est
reageEvento (EventKey (Char '-')            Down _ _) est = jogada 0 (Dispara Choque)  est
reageEvento (EventKey (Char 'w') Down _ _)            est = jogada 1 (Movimenta C)     est -- jogador 1.
reageEvento (EventKey (Char 's') Down _ _)            est = jogada 1 (Movimenta B)     est
reageEvento (EventKey (Char 'a') Down _ _)            est = jogada 1 (Movimenta E)     est
reageEvento (EventKey (Char 'd') Down _ _)            est = jogada 1 (Movimenta D)     est
reageEvento (EventKey (Char '1') Down _ _)            est = jogada 1 (Dispara Canhao)  est
reageEvento (EventKey (Char '2') Down _ _)            est = jogada 1 (Dispara Laser )  est
reageEvento (EventKey (Char '3') Down _ _)            est = jogada 1 (Dispara Choque)  est
reageEvento (EventKey (Char 't') Down _ _)            est = jogada 2 (Movimenta C)     est -- jogador 2.
reageEvento (EventKey (Char 'g') Down _ _)            est = jogada 2 (Movimenta B)     est
reageEvento (EventKey (Char 'f') Down _ _)            est = jogada 2 (Movimenta E)     est
reageEvento (EventKey (Char 'h') Down _ _)            est = jogada 2 (Movimenta D)     est
reageEvento (EventKey (Char '4') Down _ _)            est = jogada 2 (Dispara Canhao)  est
reageEvento (EventKey (Char '5') Down _ _)            est = jogada 2 (Dispara Laser )  est
reageEvento (EventKey (Char '6') Down _ _)            est = jogada 2 (Dispara Choque)  est
reageEvento (EventKey (Char 'i') Down _ _)            est = jogada 3 (Movimenta C)     est -- jogador 3.
reageEvento (EventKey (Char 'k') Down _ _)            est = jogada 3 (Movimenta B)     est
reageEvento (EventKey (Char 'j') Down _ _)            est = jogada 3 (Movimenta E)     est
reageEvento (EventKey (Char 'l') Down _ _)            est = jogada 3 (Movimenta D)     est
reageEvento (EventKey (Char '7') Down _ _)            est = jogada 3 (Dispara Canhao)  est
reageEvento (EventKey (Char '8') Down _ _)            est = jogada 3 (Dispara Laser )  est
reageEvento (EventKey (Char '9') Down _ _)            est = jogada 3 (Dispara Choque)  est
reageEvento _ s = s -- ignora qualquer outro evento
