-- | Tipo estadoGloss
type EstadoGloss = (Float,Float,Estado,[Picture],[[Picture]])

-- ** Estado Gloss Inicial
-- | Estado inicial já com gloss.
estadoGlossInicial :: [Picture] -> [[Picture]] -> EstadoGloss
estadoGlossInicial [pic,p1,p2,p3,p4,p5] [a,b,c,d,e,f]= (x,y,estadoInicial,[pic,p1,p2,p3,p4,p5],[a,b,c,d,e,f])
